package io.nebulas.explorer.domain;

import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.NoArgsConstructor;
import lombok.ToString;

import java.io.Serializable;
import java.util.Date;

/**
 * explorer.neb_address  nebulas address
 * <p>
 * Description.
 *
 * @author nathan wang
 * @version 1.0
 * @since 2018-01-25
 */
@Data
@ToString
@NoArgsConstructor
@AllArgsConstructor
public class NebAddress implements Serializable {
    /**
     * primary key, generated by program
     */
    private String id;

    /**
     * hex string of address hash
     */
    private String hash;

    /**
     * address type, 0: Normal; 1: Contract
     */
    private Integer type;

    /**
     * address alias
     */
    private String alias;

    private Date createdAt;

    private static final long serialVersionUID = 1L;
}
