package io.nebulas.explorer.domain;

import lombok.*;

import java.io.Serializable;
import java.sql.Date;

/**
 * explorer.neb_transaction  nebulas transaction
 *
 * @author nathan wang
 * @version 1.0
 * @since 2018-01-25
 */
@Builder
@Data
@ToString
@NoArgsConstructor
@AllArgsConstructor
public class NebTransaction implements Serializable {
    /**
     * primary key, generated by program
     */
    private String id;

    /**
     * hex string of transaction hash
     */
    private String hash;

    /**
     * hex string of block hash
     */
    private String blockHash;

    /**
     * block height
     */
    private Long blockHeight;

    /**
     * hex string of the sender account address
     */
    private String from;

    /**
     * hex string of the receiver account address
     */
    private String to;

    /**
     * transaction status, 0: failed; 1: success; 2: pending;
     */
    private Integer status;

    /**
     * value of transaction
     */
    private String value;

    /**
     * transaction nonce
     */
    private Long nonce;

    /**
     * transaction timestamp
     */
    private Date timestamp;

    /**
     * transaction type
     */
    private String type;

    /**
     * Gas price
     */
    private String gasPrice;

    /**
     * Gas limit
     */
    private String gasLimit;

    /**
     * Gas used
     */
    private String gasUsed;

    private Date createdAt;

    /**
     * transaction data
     */
    private String data;

    private static final long serialVersionUID = 1L;
}
